import React from 'react';


class WeatherContainer extends React.Component {
    convertTime = () => {
        let UNIX_timestamp = this.props.weatherData.date;
        var a = new Date(UNIX_timestamp * 1000);
        var months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
        var year = a.getFullYear();
        var month = months[a.getMonth()];
        var date = a.getDate();
        var hour = a.getHours();
        var min = a.getMinutes();
        var sec = a.getSeconds();
        var time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec;

    }
    render() {
        return (
            <div className="weather-container">
                <div className="date-container">
                    <div>{this.props.weatherData.date || 'Today'}</div>
                </div>
                <div className="weather-conditions">
                    {this.props.weatherData.error && <div>{this.props.weatherData.error}</div>}
                    <div>{this.props.weatherData.city || 'Dream town'}</div>
                    <div>{this.props.weatherData.temperature || '00'} Â°C</div>
                    <div>Wind: {this.props.weatherData.wind || '0'} km/h</div>
                    <div>Pressure: {this.props.weatherData.pressure || '0000'} MB</div>
                    <div>Humodity: {this.props.weatherData.humidity || '00'} %</div>
                </div>
                <div>
                    <div>Sunrise: {this.props.weatherData.sunrise || '00:00'} AM</div>
                    <div>Sunset: {this.props.weatherData.sunset || '00:00'} PM</div>
                </div>
            </div>
        );
    }
}

export default WeatherContainer;
