import React from 'react';

import SearchPanel from './components/SearchPanel';
import WeatherContainer from './components/WeatherContainer';

const API_KEY = '17c95714008c4648066caaeccfd855e7';

class App extends React.Component {
  state = {
    date: undefined,
    city: undefined,
    temperature: undefined,
    wind: undefined,
    pressure: undefined,
    humidity: undefined,
    sunrise: undefined,
    sunset: undefined,
    description: undefined,
    error: undefined
  }

  getWeather = async (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value || 'Kiev';
    const country = e.target.elements.country.value || 'ua';
    const apiCall = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=metric`);
    const data = await apiCall.json();
    console.log(data);
    this.setState({
      date: data.dt,
      city: data.name,
      temperature: data.main.temp,
      wind: data.wind.speed,
      pressure: data.main.pressure,
      humidity: data.main.humidity,
      sunrise: data.sys.sunrise,
      sunset: data.sys.sunset,
      description: data.weather[0].description,
      error: ""
    })
  }
  render() {
    return (
      <div className="weather-container">
        <SearchPanel getWeather={this.getWeather} />
        <WeatherContainer weatherData={this.state} />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
      </div>
    );
  }
}

export default App;
