import React from 'react';

class WeatherContainer extends React.Component {

        render() {
            const imageMain = {
                Rain: require('../static/rain-d.png'),
                Thunderstorm: require('../static/thunderstorm.png'),
                Drizzle: require('../static/drizzle.png'),
                Snow: require('../static/snow.png'),
                Clear: require('../static/clear-d.png'),
                Clouds: require('../static/clouds-d.png'),
                Mist: require('../static/mist.png')
            }
            const getFullTime = (time) => {
                let myDate = new Date(time * 1000);
                return myDate.toDateString()
            }

            const getTime = (time) => {
                let myTime = new Date(time * 1000);
                return myTime.getHours() + ':' + myTime.getMinutes();
            }
            return ( <
                div className = 'weather-container' >
                <
                div className = 'date-container' >
                <
                div > {
                    getFullTime(this.props.weatherData.date)
                } < /div> < /
                div > <
                div className = 'weather-conditions' >
                <
                div > {
                    this.props.weatherData.error && < div > {
                        this.props.weatherData.error
                    } < /div>} <
                    div className = 'weather-city' > {
                        this.props.weatherData.city
                    } < /div> <
                    div className = 'weather-temperature' > {
                        Math.round(this.props.weatherData.temperature)
                    }Â° < /div> <
                    div > Wind: {
                        this.props.weatherData.wind
                    }
                    km / h < /div> <
                    div > Pressure: {
                        this.props.weatherData.pressure
                    }
                    MB < /div> <
                    div > Humodity: {
                        this.props.weatherData.humidity
                    } % < /div> < /
                    div > <
                    div >
                    <
                    div className = 'weather-img' >
                    <
                    img src = {
                        imageMain[this.props.weatherData.main]
                    }
                    alt = {
                        this.props.weatherData.main
                    }
                    /> < /
                    div > <
                    div > Sunrise: {
                        getTime(this.props.weatherData.sunrise)
                    } < /div> <
                    div > Sunset: {
                        getTime(this.props.weatherData.sunset)
                    } < /div> < /
                    div > <
                    /div> < /
                    div >
                );
            }
        }

        export default WeatherContainer;
