import React from 'react';

import SearchPanel from './components/SearchPanel';
import WeatherContainer from './components/WeatherContainer';

// const API_KEY = '17c95714008c4648066caaeccfd855e7';

class App extends React.Component {
  getWeather = async (e) => {
    e.preventDefault();
    // const city = e.target.elements.city.value;
    // const country = e.target.elements.country.value;
    // const apiCall = await fetch(`api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=metrics`);
    // const data = await apiCall.json();
    const data =
      { "message": "accurate", "cod": "200", "count": 1, "list": [{ "id": 2643743, "name": "London", "coord": { "lat": 51.5085, "lon": -0.1258 }, "main": { "temp": 7, "pressure": 1012, "humidity": 81, "temp_min": 5, "temp_max": 8 }, "dt": 1485791400, "wind": { "speed": 4.6, "deg": 90 }, "sys": { "country": "GB" }, "rain": null, "snow": null, "clouds": { "all": 90 }, "weather": [{ "id": 701, "main": "Mist", "description": "mist", "icon": "50d" }, { "id": 300, "main": "Drizzle", "description": "light intensity drizzle", "icon": "09d" }] }] }

    console.log(data);
  }
  render() {
    return (
      <div className="weather-container">
        <SearchPanel getWeather={this.getWeather} />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
      </div>
    );
  }
}

export default App;
