import React from 'react';

import SearchPanel from './components/SearchPanel';
import WeatherContainer from './components/WeatherContainer';

const API_KEY = '17c95714008c4648066caaeccfd855e7';

class App extends React.Component {
  getWeather = (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    const apiCall = fetch(`api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=metrics`);
    const data = apiCall.json();
    console.log(data);
  }
  render() {
    return (
      <div className="weather-container">
        <SearchPanel getWeather={this.getWeather} />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
        <WeatherContainer />
      </div>
    );
  }
}

export default App;
